import os, zipfile, json, re, io, sys

base = "/mnt/data"

# 1) List top-level files
top_files = sorted(os.listdir(base))

# 2) Inspect ZIP contents if present
zip_path = os.path.join(base, "AREN_TRANSCEND_001.zip")
zip_listing = []
if os.path.exists(zip_path):
    with zipfile.ZipFile(zip_path, 'r') as z:
        zip_listing = sorted(z.namelist())

# 3) Quick scan for names that look like "Ideen f√ºr Roman" (case-insensitive, allow ascii variants)
patterns = [
    re.compile(r"ideen\s*f(√º|u)r\s*roman", re.IGNORECASE),
    re.compile(r"roman[_\-\s]*ideen", re.IGNORECASE),
]

def match_any(name):
    return any(p.search(name) for p in patterns)

matches_fs = [f for f in top_files if match_any(f)]
matches_zip = [n for n in zip_listing if match_any(n)]

# 4) Also search inside CHASEN_KEY_vNEXT.json for any field that mentions such a label
json_path = os.path.join(base, "CHASEN_KEY_vNEXT.json")
json_hits = []
if os.path.exists(json_path):
    try:
        with open(json_path, "r", encoding="utf-8") as f:
            data = json.load(f)
        # recursive search for strings containing the phrase
        def walk(obj, path=""):
            if isinstance(obj, dict):
                for k,v in obj.items():
                    walk(v, f"{path}.{k}" if path else k)
            elif isinstance(obj, list):
                for i,v in enumerate(obj):
                    walk(v, f"{path}[{i}]")
            elif isinstance(obj, str):
                if match_any(obj):
                    json_hits.append((path, obj))
        walk(data)
    except Exception as e:
        json_hits.append(("__error__", str(e)))

# 5) Build a small textual report
report = {
    "top_level_files": top_files,
    "zip_contains": zip_listing[:50] + (["..."] if len(zip_listing) > 50 else []),
    "matches_in_filesystem": matches_fs,
    "matches_in_zip": matches_zip,
    "json_hits": json_hits[:10],
}

report

