# Wir normalisieren: wenn es Dicts sind, extrahieren wir den Text.

def extract_parts(parts):
    texts = []
    for p in parts:
        if isinstance(p, str):
            texts.append(p)
        elif isinstance(p, dict):
            # Versuch, Textfelder aus Dicts zu ziehen
            if "text" in p:
                texts.append(p["text"])
            elif "content" in p and isinstance(p["content"], str):
                texts.append(p["content"])
    return "\n".join(texts).strip()

