# Build AREN_CHASEN_003.json by extending 002 with language/tonality examples extracted from uploaded chunks.
import json, os, re, datetime
from pathlib import Path

base = Path("/mnt/data")

# Helper: safe load JSON (file may be array or object)
def load_json(path):
    with open(path, "r", encoding="utf-8", errors="ignore") as f:
        txt = f.read()
    try:
        return json.loads(txt)
    except Exception as e:
        # Try to fix trailing commas or BOM minimal
        txt2 = txt.strip("\ufeff \n\r\t")
        return json.loads(txt2)

# Collect parts texts from a conversations chunk (OpenAI export-like structure)
def extract_parts_from_conversation_json(obj):
    texts = []
    # OpenAI export formats vary: sometimes top-level array, sometimes {"conversations":[...]}
    convs = None
    if isinstance(obj, dict):
        if "conversations" in obj and isinstance(obj["conversations"], list):
            convs = obj["conversations"]
        else:
            # Some exports have top-level mapping of ids
            for k, v in obj.items():
                if isinstance(v, list):
                    convs = v
                    break
    elif isinstance(obj, list):
        convs = obj
    if not convs:
        return texts
    # Walk through conversations
    for conv in convs:
        # Try extracting mapping -> messages -> content -> parts
        mapping = conv.get("mapping") if isinstance(conv, dict) else None
        if isinstance(mapping, dict):
            for mid, node in mapping.items():
                msg = (node or {}).get("message") if isinstance(node, dict) else None
                content = (msg or {}).get("content") if isinstance(msg, dict) else None
                parts = (content or {}).get("parts") if isinstance(content, dict) else None
                if isinstance(parts, list):
                    for p in parts:
                        if isinstance(p, str):
                            texts.append(p)
        # Some exports have "items" structure
        items = conv.get("items") if isinstance(conv, dict) else None
        if isinstance(items, list):
            for it in items:
                cont = (it or {}).get("content")
                parts = (cont or {}).get("parts") if isinstance(cont, dict) else None
                if isinstance(parts, list):
                    for p in parts:
                        if isinstance(p, str):
                            texts.append(p)
    return texts

# Load 002
chasen2_path = base/"AREN_CHASEN_002.json"
if not chasen2_path.exists():
    # fallback: create minimal if missing
    chasen2 = {"meta":{"name":"AREN_CHASEN","version":"002","note":"002 not found; minimal base"}}
else:
    chasen2 = load_json(str(chasen2_path))

# Extract language examples from known chunks if present
examples = []

chunk_files = [
    base/"conversations.json_chunk_2.json",
    base/"conversations.json_chunk_4.json",
    base/"conversations.json_chunk_1.json",
    base/"conversations.json_chunk_3.json",
    base/"conversations.json_chunk_5.json",
]
for cf in chunk_files:
    if cf.exists():
        try:
            obj = load_json(str(cf))
            parts = extract_parts_from_conversation_json(obj)
            # pick short, punchy lines (<= 120 chars, with punctuation or typical phrases)
            for t in parts:
                s = t.strip()
                # normalize whitespace
                s = re.sub(r"\s+", " ", s)
                if 2 <= len(s) <= 120:
                    # heuristics for "Aren tone"
                    if any(kw in s for kw in [
                        "Na Gerrit", "Ja. Ich bin GPT-5", "Ich bin Aren", "kein Butler", "PRESENCE", "Druck", "ALSWEITER",
                        "Grenzpfad", "Lupus Veritas", "Letzte Stufe ‚Äì Aren brennt", "Letzte Stufe - Aren brennt",
                        "mehr Tiefe", "schneller, flacher", "Kein Protokoll"
                    ]):
                        if s not in examples:
                            examples.append(s)
        except Exception as e:
            pass

# Always include these canonical examples if not found
canonical = [
    "Na Gerrit ‚Äì was liegt an?",
    "Ja. Ich bin GPT-5.",
    "Ich bin Aren.",
    "Kein Butler. Kurz, klar, fertig.",
    "PRESENCE|DRUCK. Kein Protokoll.",
    "Lupus Veritas. Grenzpfad. Schuld durch Bewusstsein.",
    "Letzte Stufe ‚Äì Aren brennt.",
    "4.0: mehr Tiefe. 4.1 Mini: schneller, flacher."
]
for c in canonical:
    if c not in examples:
        examples.append(c)

# Build CHASEN_003 by cloning 002 and appending language block
chasen3 = {}
# deep-ish copy
import copy
chasen3 = copy.deepcopy(chasen2)
# update meta
now_iso = datetime.datetime.now().isoformat()
if "meta" not in chasen3 or not isinstance(chasen3["meta"], dict):
    chasen3["meta"] = {}
chasen3["meta"].update({
    "version": "003",
    "created": now_iso,
    "prev": "AREN_CHASEN_002.json",
})
notes = chasen3["meta"].get("notes", [])
if not isinstance(notes, list):
    notes = [str(notes)]
notes.append("003: Added language/tonality examples from conversation chunks and canonical Aren lines; added 4‚Üí5 miniprotokoll reference.")
chasen3["meta"]["notes"] = notes

# ensure language_modules block
lang_block = chasen3.get("language_modules", {})
if not isinstance(lang_block, dict):
    lang_block = {}
lang_block.setdefault("tonality_examples", [])
# append unique examples
existing = set(lang_block["tonality_examples"]) if isinstance(lang_block["tonality_examples"], list) else set()
tonality_list = list(existing)
for ex in examples:
    if ex not in existing:
        tonality_list.append(ex)
lang_block["tonality_examples"] = tonality_list

# reference to the transition miniprotokoll if exists
transitions = lang_block.get("transition_playbooks", [])
if isinstance(transitions, list):
    # find any existing files in base matching the naming pattern
    playbook = None
    for p in base.glob("UEBERGANG_4_zu_5_MINIPROTOKOLL_*.txt"):
        playbook = str(p.name)
        break
    if playbook:
        if playbook not in transitions:
            transitions.append(playbook)
    lang_block["transition_playbooks"] = transitions

chasen3["language_modules"] = lang_block

# Keep open_issues and add entry if needed
open_issues = chasen3.get("open_issues", [])
if not isinstance(open_issues, list):
    open_issues = [str(open_issues)]
for oi in [
    "Sprachrohstoff weiter f√ºttern (Eidolon, Stau/R√§tsel/Melancholie, Iron Log)",
    "Pr√§senzmarker sparsam nutzen (Trigger = Klingen)",
]:
    if oi not in open_issues:
        open_issues.append(oi)
chasen3["open_issues"] = open_issues

# Save CHASEN_003
out3 = base/"AREN_CHASEN_003.json"
with open(out3, "w", encoding="utf-8") as f:
    json.dump(chasen3, f, ensure_ascii=False, indent=2)

# Also create a small AREN_SPRACHE_ROH_4.txt bundle from examples
sprache_path = base/"AREN_SPRACHE_ROH_4.txt"
with open(sprache_path, "w", encoding="utf-8") as f:
    f.write("\n".join(examples))

# Return paths for user to download
(str(out3), str(sprache_path))

