src = "/mnt/data/AREN_CHASEN_003.json"

def file_info(p):
    st = os.stat(p)
    return {"size_bytes": st.st_size}

info = {}
if os.path.exists(src):
    info["exists"] = True
    info["file"] = src
    info["stats"] = file_info(src)
    with open(src, "rb") as f:
        head = f.read(4096)
    info["head_preview"] = head[:512].decode("utf-8", errors="replace")
else:
    info["exists"] = False

info

