    "Ready": r"Ready|Ready-Signal|Ready Signal",
    "Recovery": r"Recovery",
    "Zirkus": r"Zirkus",
    "Humor": r"Humor",
    "acht": r"\bacht\b|\b8\b",
    "Startblock": r"Startblock_?0?0?3|Startblock_?0?0?4|Startblock",
}
results = {k: find(v) for k,v in patterns.items()}
{k:(c, n) for k,(c,n) in results.items()}

