from docx import Document

# Inhalte der Resetframe-Dateien einlesen
resetframe_docs = {}
for file in extract_dir_resetframe.rglob("*.docx"):
    try:
        doc = Document(file)
        text = "\n".join([p.text for p in doc.paragraphs if p.text.strip()])
        resetframe_docs[file.name] = text
    except Exception as e:
        resetframe_docs[file.name] = f"ERROR: {str(e)}"

# Erste drei Inhalte zur Pr√ºfung anzeigen
list(resetframe_docs.items())[:3]

