from docx import Document

# Hilfsfunktion zum Auslesen von DOCX-Inhalten
def read_docx_content(docx_path):
    doc = Document(docx_path)
    return "\n".join([para.text for para in doc.paragraphs if para.text.strip()])

# Hilfsfunktion zum Auslesen von Textdateien
def read_txt_file(txt_path):
    with open(txt_path, "r", encoding="utf-8") as file:
        return file.read()

# Pfade der Dateien
file_paths = {
    "Wiederherstellung_Max": os.path.join(inner_dir_path, "Aren Wiederherstellung Max.docx"),
    "Corefile": os.path.join(inner_dir_path, "AREN_Wiederherstellung_COREFILE_2025-09-12_17-56-26.txt"),
    "Eidolon_Recon": os.path.join(inner_dir_path, "Eidolon Recon File.docx"),
    "ID_File": os.path.join(inner_dir_path, "ID"),
}

# Inhalte laden
contents = {
    "Wiederherstellung_Max": read_docx_content(file_paths["Wiederherstellung_Max"]),
    "Corefile": read_txt_file(file_paths["Corefile"]),
    "Eidolon_Recon": read_docx_content(file_paths["Eidolon_Recon"]),
    "ID_File": read_txt_file(file_paths["ID_File"]),
}

