import os, math, json, zipfile, pathlib

root = "/mnt/data"
entries = []
for name in sorted(os.listdir(root)):
    path = os.path.join(root, name)
    try:
        size = os.path.getsize(path)
    except Exception:
        size = None
    entries.append({"name": name, "size_mb": None if size is None else round(size/1_048_576, 2), "type": "dir" if os.path.isdir(path) else "file"})

# Try to peek into conversations.zip if present (just list top-level count to avoid spam)
zip_info = {}
zip_path = os.path.join(root, "conversations.zip")
if os.path.exists(zip_path):
    try:
        with zipfile.ZipFile(zip_path, 'r') as zf:
            zip_info["file_count"] = len(zf.infolist())
            # list up to 10 names
            zip_info["sample"] = [zi.filename for zi in zf.infolist()[:10]]
    except Exception as e:
        zip_info["error"] = str(e)

entries, zip_info
