def extract_text(msg):
    parts = msg.get("content", {}).get("parts")
    if not parts:
        return None
    out = []
    for p in parts:
        if isinstance(p, str):
            out.append(p)
        elif isinstance(p, dict):
            # Falls verschachtelt, versuchen wir 'text' Key zu ziehen
            out.append(p.get("text", ""))
    return " ".join(out).strip()

